/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `add_menu_role`(IN roleCode VARCHAR(30),IN resourcesIDs VARCHAR(5000),IN createQX INT(3),IN readQX INT(3),IN updateQX INT(3),IN deleteQX INT(3),IN exportQX INT(3))
BEGIN
DECLARE var INT
;
DECLARE total INT DEFAULT 0;
DECLARE str VARCHAR(5000);
DECLARE str1 VARCHAR(2000);
SET str=getString(resourcesIDs);
SET var=1;
SET total=a(str,',');
WHILE var<=total DO
SET str1=func_splitString(func_splitString(str,',',var),'=',2);
INSERT INTO e_role_permission(role_code,menu_id,auth_create,auth_read,auth_update,auth_delete,auth_export) VALUES(roleCode,str1,createQX,readQX,updateQX,deleteQX,exportQX);
INSERT INTO e_role_permission(role_code,menu_id,auth_create,auth_read,auth_update,auth_delete,auth_export)
SELECT roleCode, T2.RESOURCES_ID, createQX, readQX, updateQX, deleteQX, exportQX
FROM ( 
    SELECT 
        @r AS _id, 
        (SELECT @r := PARENT_ID FROM e_menu WHERE RESOURCES_ID = _id) AS PARENT_ID, 
        @l := @l + 1 AS lvl 
    FROM 
        (SELECT @r :=str1, @l := 0) vars, 
        e_menu h 
    WHERE @r >= 0) T1 
JOIN e_menu T2 
ON T1._id = T2.RESOURCES_ID
WHERE NOT EXISTS (SELECT * FROM  e_role_permission T3 WHERE T1._id = T3.MENU_ID AND T3.ROLE_CODE = roleCode)
ORDER BY T1.lvl DESC;
SET var=var+1;
END WHILE;
END */$$
